import React, { Component } from 'react';
import './App.css';

class App extends Component {
    state = {
      Texts: [
        { text: "Hello"},
        {text: ""}
      ]

    }

    lengthCalcHandler = (event) => {
        this.setState({
          Texts: [
            { text: "Hello"},
            { text: event.target.value, leng: event.target.value.length }
          ]
        })
    }


  render() {
    return (
      <div className="App">
        <p>The text is {this.state.Texts[0].text}</p>
        <input type="text" placeholder="Enter your text" onChange = {this.lengthCalcHandler} />
        <p value={this.lengthCalcHandler.length}>The length is  </p>
        <ol>
          <li>Create an input field (in App component) with a change listener which outputs the length of the entered text below it (e.g. in a paragraph).</li>
          <li>Create a new component (=> ValidationComponent) which receives the text length as a prop</li>
          <li>Inside the ValidationComponent, either output "Text too short" or "Text long enough" depending on the text length (e.g. take 5 as a minimum length)</li>
          <li>Create another component (=> CharComponent) and style it as an inline box (=> display: inline-block, padding: 16px, text-align: center, margin: 16px, border: 1px solid black).</li>
          <li>Render a list of CharComponents where each CharComponent receives a different letter of the entered text (in the initial input field) as a prop.</li>
          <li>When you click a CharComponent, it should be removed from the entered text.</li>
        </ol>
        <p>Hint: Keep in mind that JavaScript strings are basically arrays!</p>
      </div>
    );
  }
}

export default App;
